<template>
	<transition name="notificationAnm">
		<div class="notification" :style="style" v-if="visible">
			<div class="content">
				<p class="title">
					<span>
						<i :class="typeclass"></i>{{title}}
					</span>
					<b @click="close"></b>
				</p>
				<p class="content">{{content}}</p>
			</div>
		</div>
	</transition>
</template>

<script>
export default {
	name: 'notification',
	data () {
		return {
			title : '',
			content : '',
			type : '',
			onClose : null,
			visible : true,
			isclose : false,
			posTop : '',
			typearray : ['msg' ,'error' ,'success' ,'warning']
		}
	},
	watch : {
		isclose (newVal){
			if(newVal){
				this.visible = false;
			}
		}
	},
	computed : {
		style (){
			return {
				top : `${this.posTop}px`
			}
		},
		typeclass (){ 
			return this.typearray.includes(this.type) ? `icon ${this.type}` : '';
		}
	},
	methods : {
		close (){
			this.isclose = true;
			this.onClose();
		}
	}
}
</script>

